import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

let gameOptions = {
    numberRows: 2,
    numberColumns: 2,
    complexity: 5,
}

class Square extends React.Component {
    activateButton = function (e) {
        if (this.props.flickeringMemory.length > 0) {
            if ((parseInt(this.props.flickeringMemory[0].id[0]) === this.props.coords[0]) && (parseInt(this.props.flickeringMemory[0].id[2]) === this.props.coords[1])) {
                e.target.style.backgroundColor = 'white'
                this.props.flickeringMemory.shift()
                setTimeout(function () {
                    e.target.style.backgroundColor = '#799E00'
                })
            }
        }
    }
    activateButton = this.activateButton.bind(this)
    render() {
        return (
            <button className="square" id={this.props.coords[0] + ":" + this.props.coords[1]} onClick={this.activateButton}>
                {this.props.value}
            </button>
        );
    }
}

class Row extends React.Component {
    renderSquares = function (counter, rowNumber) {
        let renderArray = []
        for (let i = 0; i < counter; i++) {
            renderArray.push(<Square key = {i} coords = {[rowNumber, i]} flickeringMemory = {this.props.flickeringMemory}/>)
        }
        return renderArray
    }
    render() {
        return (
            <div className="board-row">
                {this.renderSquares(gameOptions.numberColumns, this.props.value)}
            </div>
        )
    }
}

class Board extends React.Component {
    state = {
        flickeringMemory: [],
        levelPassed: true,
    }
    clickHandler = function () {
        // noinspection JSPotentiallyInvalidUsageOfClassThis
        if (this.state.levelPassed === true) {
            // noinspection JSPotentiallyInvalidUsageOfClassThis
            this.setState({
                levelPassed: false,
                flickeringMemory: this.startFlickering(),
            })
        }
    }
    clickHandler = this.clickHandler.bind(this)
    renderRows = function (counter) {
        let renderArray = []
        for (let i = 0; i < counter; i++) {
            renderArray.push(<Row key={i} value = {i} flickeringMemory = {this.state.flickeringMemory}/>)
        }
        return renderArray
    }
    randomFlicker = function (root) {
        let elementsArray = Array.from(root.querySelectorAll('.square'))
        let randomNumbers = []
        let randomFlickeringArray = []
        let min = 0
        let max = gameOptions.numberColumns*gameOptions.numberRows - 1
        for (let i = 0; i < gameOptions.complexity; i++) {
            let randomIndex = Math.floor(Math.random() * (max - min + 1)) + min
            randomNumbers.push(randomIndex)
        }
        for (let index of randomNumbers) {
            randomFlickeringArray.push(elementsArray[index])
        }
        return randomFlickeringArray
    }

    startFlickering = function () {
        let flickeringArray = this.randomFlicker(document.getElementById('root'))
        for (let i = 0; i < flickeringArray.length; i++) {
            let timeout = i === 0? 0 : i * 1500
            setTimeout(function () {
                flickeringArray[i].style.background = 'white'
            }, timeout)
            setTimeout(function () {
                flickeringArray[i].style.background = '#799E00'
            }, timeout+900)
        }
        return flickeringArray
    }
    startFlickering = this.startFlickering.bind(this)
    render() {
        return (
            <div>
                {this.renderRows(gameOptions.numberRows)}
                <button className={'startButton'} onClick={this.clickHandler}>
                    Start
                </button>
            </div>
        );
    }
}

class Game extends React.Component {
    render() {
        return (
            <div className="game">
                <div className="game-board">
                    <Board />
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Game />,
    document.getElementById('root')
);
