import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

let gameOptions = {
    numberRows: 4,
    numberColumns: 4,
    complexity: 5,
}

function randomFlicker (root) {
    let elementsArray = Array.from(root.querySelectorAll('.square'))
    elementsArray.sort(() => Math.random() - 0.5)
    return elementsArray.slice(0, gameOptions.complexity)
}

function startFlickering() {
    let flickeringArray = randomFlicker(document.getElementById('root'))
    for (let i = 0; i < flickeringArray.length; i++) {
        let timeout = i === 0? 0 : i * 1000
        setTimeout(function () {
            flickeringArray[i].style.background = 'white'
        }, timeout)
        setTimeout(function () {
            flickeringArray[i].style.background = '#799E00'
        }, timeout+900)
    }
}

function activateButton(e) {
    e.target.style.backgroundColor = 'white'
}

function renderSquares(counter, rowNumber) {
    let renderArray = []
    for (let i = 0; i < counter; i++) {
        renderArray.push(<Square key = {i} column = {i} row = {rowNumber} />)
    }
    return renderArray
}
function renderRows(counter) {
    let renderArray = []
    for (let i = 0; i < counter; i++) {
        renderArray.push(<Row key={i} value = {i} />)
    }
    return renderArray
}

class Start extends React.Component {
    render() {
        return (
            <button className={'startButton'} onClick={startFlickering}>
                Start
            </button>
        )
    }
}

class Square extends React.Component {
    render() {
        return (
            <button className="square">
                {this.props.value}
            </button>
        );
    }
}

class Row extends React.Component {
    render() {
        return (
            <div className="board-row">
                {renderSquares(gameOptions.numberColumns, this.props.value)}
            </div>
        )
    }
}

class Board extends React.Component {
    state
    clickHandler = function () {

    }
    render() {
        return (
            <div>
                {renderRows(gameOptions.numberRows)}
                <Start onClick={this.clickHandler()}/>
            </div>
        );
    }
}

class Game extends React.Component {
    render() {
        return (
            <div className="game">
                <div className="game-board">
                    <Board />
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Game />,
    document.getElementById('root')
);
