import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

let gameOptions = {
    numberRows: 4,
    numberColumns: 4,
    complexity: 5,
}

function randomFlicker (root) {
    let elementsArray = Array.from(root.querySelectorAll('.square'))
    elementsArray.sort(() => Math.random() - 0.5)
    return elementsArray.slice(0, gameOptions.complexity)
}

function startFlickering() {
    let flickeringArray = randomFlicker(document.getElementById('root'))
    for (let i = 0; i < flickeringArray.length; i++) {
        let timeout = i === 0? 0 : i * 1000
        setTimeout(function () {
            flickeringArray[i].style.background = 'white'
        }, timeout)
        setTimeout(function () {
            flickeringArray[i].style.background = '#799E00'
        }, timeout+900)
    }
    return flickeringArray
}

function activateButton(e) {
    e.target.style.backgroundColor = 'white'
}

function renderSquares(counter, rowNumber) {
    let renderArray = []
    for (let i = 0; i < counter; i++) {
        renderArray.push(<Square key = {i} coords = {[i, rowNumber]} />)
    }
    return renderArray
}
function renderRows(counter) {
    let renderArray = []
    for (let i = 0; i < counter; i++) {
        renderArray.push(<Row key={i} value = {i} />)
    }
    return renderArray
}

class Square extends React.Component {
    constructor(props) {
        super(props);
        this.coords = props.coords
    }
    render() {
        return (
            <button className="square" id={this.coords} onClick={activateButton}>
                {this.props.value}
            </button>
        );
    }
}

class Row extends React.Component {
    render() {
        return (
            <div className="board-row">
                {renderSquares(gameOptions.numberColumns, this.props.value)}
            </div>
        )
    }
}

class Board extends React.Component {
    state = {
        flickeringMemory: []
    }
    clickHandler = function () {
        this.setState({ flickeringMemory: startFlickering() })
    }
    clickHandler = this.clickHandler.bind(this)
    render() {
        return (
            <div>
                {renderRows(gameOptions.numberRows)}
                <button className={'startButton'} onClick={this.clickHandler}>
                    Start
                </button>
            </div>
        );
    }
}

class Game extends React.Component {
    render() {
        return (
            <div className="game">
                <div className="game-board">
                    <Board />
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Game />,
    document.getElementById('root')
);
