import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

let gameOptions = {
    numberRows: 4,
    numberColumns: 4,
}

function renderSquares(counter) {
    let renderArray = []
    for (let i = 0; i < counter; i++) {
        renderArray.push(<Square key = {i} value = {this.state.squares[i]}/>)
    }
    return renderArray
}
function renderRows(counter) {
    let renderArray = []
    for (let i = 0; i < counter; i++) {
        renderArray.push(<Row key={i} />)
    }
    return renderArray
}


class Square extends React.Component {
    render() {
        return (
            <button className="square">
                {this.props.value}
            </button>
        );
    }
}

class Row extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            squares: Array(gameOptions.numberRows*gameOptions.numberColumns).fill(null)
        }
    }
    render() {
        return (
            <div className="board-row">
                {renderSquares(gameOptions.numberColumns)}
            </div>
        )
    }
}

class Board extends React.Component {
    render() {
        return (
            <div>
                {renderRows(gameOptions.numberRows)}
            </div>
        );
    }
}

class Game extends React.Component {
    render() {
        return (
            <div className="game">
                <div className="game-board">
                    <Board />
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Game />,
    document.getElementById('root')
);
