import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

let gameOptions = {
    numberRows: 4,
    numberColumns: 4,
    complexity: 5,
}

function activateButton(e) {
    e.target.style.backgroundColor = 'white'
}

function renderSquares(counter, rowNumber) {
    let renderArray = []
    for (let i = 0; i < counter; i++) {
        renderArray.push(<Square key = {i} column = {i} row = {rowNumber} />)
    }
    return renderArray
}
function renderRows(counter) {
    let renderArray = []
    for (let i = 0; i < counter; i++) {
        renderArray.push(<Row key={i} value = {i} />)
    }
    return renderArray
}

class Start extends React.Component {
    render() {
        return (
            <button className={'startButton'}>
                Start
            </button>
        )
    }
}

class Square extends React.Component {
    render() {
        return (
            <button className="square" onClick={activateButton}>
                {this.props.value}
            </button>
        );
    }
}

class Row extends React.Component {
    render() {
        return (
            <div className="board-row">
                {renderSquares(gameOptions.numberColumns, this.props.value)}
            </div>
        )
    }
}

class Board extends React.Component {
    render() {
        return (
            <div>
                {renderRows(gameOptions.numberRows)}
                <Start />
            </div>
        );
    }
}

class Game extends React.Component {
    render() {
        return (
            <div className="game">
                <div className="game-board">
                    <Board />
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Game />,
    document.getElementById('root')
);

function randomFlicker (root) {
    let elementsArray = Array.from(root.querySelectorAll('.square'))
    elementsArray.sort()
        for (let element of elementsNodeList) {
            if (flickeringArray.length === gameOptions.complexity) {
                break
            }
            else {
                let randomizer = Math.random()
                if (randomizer < 0.5) {
                    flickeringArray.push(element)
                }
            }
        }
    alert(flickeringArray)
}
