import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

let gameOptions = {
    numberRows: 4,
    numberColumns: 4,
}

function activateButton(e) {
    e.target.style.backgroundColor = 'white'
}

function renderSquares(counter, rowNumber) {
    let renderArray = []
    for (let i = 0; i < counter; i++) {
        renderArray.push(<Square key = {i} column = {i} row = {rowNumber} />)
    }
    return renderArray
}
function renderRows(counter) {
    let renderArray = []
    for (let i = 0; i < counter; i++) {
        renderArray.push(<Row key={i} value = {i} />)
    }
    return renderArray
}


class Square extends React.Component {
    render() {
        return (
            <button className="square" onClick={activateButton}>
                {this.props.value}
            </button>
        );
    }
}

class Row extends React.Component {
    render() {
        return (
            <div className="board-row">
                {renderSquares(gameOptions.numberColumns, this.props.value)}
            </div>
        )
    }
}

class Board extends React.Component {
    render() {
        return (
            <div>
                {renderRows(gameOptions.numberRows)}
            </div>
        );
    }
}

class Game extends React.Component {
    render() {
        return (
            <div className="game">
                <div className="game-board">
                    <Board />
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <Game />,
    document.getElementById('root')
);

document.onload = function () {

}
